-- ----------------------------
-- Table structure for `continent`
-- ----------------------------
DROP TABLE IF EXISTS esgf_dashboard.continent;
CREATE TABLE esgf_dashboard.continent (
  continent_code character(2) PRIMARY KEY,
  continent_name character varying(255) DEFAULT NULL
);

-- ----------------------------
-- Records of continents
-- ----------------------------

DROP TABLE IF EXISTS esgf_dashboard.countries;
CREATE TABLE esgf_dashboard.countries (
  country_id serial PRIMARY KEY,
  country_code character(2) NOT NULL,
  country_name character varying(64) NOT NULL,
  country_full_name character varying(128) NOT NULL,
  country_iso3 character(3) NOT NULL,
  country_number smallint NOT NULL,
  continent_code character(2) NOT NULL REFERENCES esgf_dashboard.continent,
  display_order integer NOT NULL DEFAULT '900'
);

